// We'll load the OAuth credentials from the file
let oauthCredentials = null;

async function loadOAuthCredentials() {
  if (oauthCredentials) return oauthCredentials;

  try {
    // Try both paths - with and without Ka-Piki prefix for local and production
    const paths = [
      "/client_secret_811771021125-37nptsve9d17qjgecm2kb8rrm387ieeh.apps.googleusercontent.com.json",
      "/Ka-Piki/client_secret_811771021125-37nptsve9d17qjgecm2kb8rrm387ieeh.apps.googleusercontent.com.json",
    ];

    let response;
    for (const path of paths) {
      response = await fetch(path);
      if (response.ok) break;
    }

    if (!response.ok) {
      const text = await response.text();
      console.error("Failed to load credentials, response:", text);
      throw new Error(`Failed to load OAuth credentials: ${response.status}`);
    }

    oauthCredentials = await response.json();
    console.log("Successfully loaded OAuth credentials");
    return oauthCredentials;
  } catch (error) {
    console.error("Error loading OAuth credentials:", error);
    throw error;
  }
}

const OAUTH_REDIRECT_URI = window.location.origin + "/oauth-callback";
const SENDER_EMAIL = "peta@kapiki.co.nz";

// Function to handle Google OAuth login - you'll only need to do this once
export async function initializeGmailAuth() {
  const credentials = await loadOAuthCredentials();
  const authUrl = `https://accounts.google.com/o/oauth2/v2/auth?client_id=${
    credentials.web.client_id
  }&redirect_uri=${encodeURIComponent(
    OAUTH_REDIRECT_URI
  )}&response_type=code&access_type=offline&prompt=consent&scope=https://www.googleapis.com/auth/gmail.send`;

  // Store the current page URL so we can return after auth
  localStorage.setItem("emailAuthReturnTo", window.location.pathname);

  // Redirect to Google login
  window.location.href = authUrl;
}

// Function to get access token using the refresh token
async function getAccessToken() {
  const refreshToken = localStorage.getItem("gmail_refresh_token");
  if (!refreshToken) {
    initializeGmailAuth();
    return null;
  }

  const credentials = await loadOAuthCredentials();
  const response = await fetch("https://oauth2.googleapis.com/token", {
    method: "POST",
    headers: {
      "Content-Type": "application/x-www-form-urlencoded",
    },
    body: new URLSearchParams({
      client_id: credentials.web.client_id,
      client_secret: credentials.web.client_secret,
      refresh_token: refreshToken,
      grant_type: "refresh_token",
    }),
  });

  if (!response.ok) {
    // If refresh token is invalid, we need to re-authenticate
    localStorage.removeItem("gmail_refresh_token");
    initializeGmailAuth();
    return null;
  }

  const data = await response.json();
  return data.access_token;
}

export async function handleAuthCallback(code) {
  const credentials = await loadOAuthCredentials();
  const response = await fetch("https://oauth2.googleapis.com/token", {
    method: "POST",
    headers: {
      "Content-Type": "application/x-www-form-urlencoded",
    },
    body: new URLSearchParams({
      code,
      client_id: credentials.web.client_id,
      client_secret: credentials.web.client_secret,
      redirect_uri: OAUTH_REDIRECT_URI,
      grant_type: "authorization_code",
    }),
  });

  const data = await response.json();
  localStorage.setItem("gmail_refresh_token", data.refresh_token);
  return data.access_token;
}

export async function sendEmail(recipientEmails, subject, messageText) {
  try {
    // Log the raw data we received
    console.log("Raw email data:", {
      to: recipientEmails,
      subject: subject,
      message: messageText,
    });

    // Get a valid access token
    const accessToken = await getAccessToken();
    if (!accessToken) return; // This will trigger re-auth if needed

    // Create email message
    const message = createMessage(recipientEmails, subject, messageText);

    // Log the formatted message before encoding
    const decodedMessage = atob(message);
    console.log("Formatted email before sending:", decodedMessage);

    // Send email using Gmail API with your email address
    const response = await fetch(
      `https://gmail.googleapis.com/gmail/v1/users/${encodeURIComponent(
        SENDER_EMAIL
      )}/messages/send`,
      {
        method: "POST",
        headers: {
          Authorization: `Bearer ${accessToken}`,
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ raw: message }),
      }
    );

    const responseText = await response.text();
    console.log("Gmail API response:", response.status, responseText);

    if (!response.ok) {
      console.error("Gmail API error response:", responseText);
      try {
        const errorData = JSON.parse(responseText);
        throw new Error(errorData.error?.message || "Failed to send email");
      } catch (e) {
        throw new Error("Failed to send email: " + responseText);
      }
    }

    return { success: true, response: responseText };
  } catch (error) {
    console.error("Error sending email:", error);
    throw error;
  }
}

function createMessage(recipientEmails, subject, messageText) {
  // Format the email headers properly
  const headers = {
    "MIME-Version": "1.0",
    "Content-Type": 'text/plain; charset="UTF-8"',
    "Content-Transfer-Encoding": "7bit",
    From: SENDER_EMAIL,
    To: recipientEmails.join(","),
    Subject: subject,
  };

  // Build the email with proper headers
  const email =
    Object.entries(headers)
      .map(([key, value]) => `${key}: ${value}`)
      .join("\r\n") +
    "\r\n\r\n" +
    messageText;

  // Base64URL encode the email
  const base64Email = btoa(email)
    .replace(/\+/g, "-")
    .replace(/\//g, "_")
    .replace(/=+$/, "");

  console.log("Created email:", {
    headers,
    messageText,
    encodedLength: base64Email.length,
  });

  return base64Email;
}
