import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import { sendEmail, hasValidCredentials, startOAuthFlow } from "../services/emailService";
import { useAuth } from "../contexts/AuthContext";
import Airtable from "airtable";

// Debug the environment variables
console.log('Raw env values:', {
  key: process.env.REACT_APP_AIRTABLE_API_KEY,
  base: process.env.REACT_APP_AIRTABLE_BASE_ID
});

// Initialize Airtable the simple way that worked before
const base = new Airtable({
  apiKey: 'patuHJGGTaXKERLC8.4f23e14326051a35f41bce0041488811711e1d2cad67892e767744474fa107cb'
}).base('app054uJcxxiTUK7p');

export default function ClientsPage() {
  const [clients, setClients] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [isSending, setIsSending] = useState(false);
  const [selectedClient, setSelectedClient] = useState(null);
  const [emailContent, setEmailContent] = useState({
    subject: "",
    body: "",
  });
  const [showEmailModal, setShowEmailModal] = useState(false);
  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");
  const navigate = useNavigate();
  const { user } = useAuth();

  useEffect(() => {
    const checkCredentials = async () => {
      try {
        const isValid = await hasValidCredentials();
        console.log("Gmail credentials status:", isValid ? "Valid" : "Not valid");

        if (!isValid) {
          await startOAuthFlow();
          return;
        }

        setIsLoading(false);
      } catch (error) {
        console.error("Error checking credentials:", error);
        setIsLoading(false);
      }
    };

    checkCredentials();
  }, [navigate]);

  useEffect(() => {
    const fetchClients = async () => {
      try {
        console.log('Attempting to fetch from Airtable...');
        const records = await base('EmailTemplate').select({
          view: 'Grid view'
        }).all();

        console.log('Got records:', records);

        const clientsData = records.map(record => ({
          id: record.id,
          name: record.fields.Name || "",
          email: record.fields.Email || "",
          phone: record.fields.Phone || "",
          status: record.fields.Status || "",
          notes: record.fields.Notes || "",
        }));

        setClients(clientsData);
      } catch (error) {
        console.error("Error fetching clients:", error);
        console.error("Full error object:", JSON.stringify(error, null, 2));
        setError("Failed to fetch clients: " + error.message);
      }
    };

    fetchClients();
  }, []);

  const handleSendEmail = async () => {
    if (!selectedClient) return;

    setIsSending(true);
    setError("");
    setSuccess("");

    try {
      await sendEmail(
        selectedClient.email,
        emailContent.subject,
        emailContent.body
      );
      setSuccess("Email sent successfully!");
      setShowEmailModal(false);
      setEmailContent({ subject: "", body: "" });
    } catch (error) {
      console.error("Error sending email:", error);
      setError(error.message || "Failed to send email");
    } finally {
      setIsSending(false);
    }
  };

  if (isLoading) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-500 mx-auto"></div>
          <p className="mt-4 text-gray-600">Checking Gmail credentials...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="container mx-auto px-4 py-8">
      <h1 className="text-3xl font-bold mb-8">Clients</h1>

      {error && (
        <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded mb-4">
          {error}
        </div>
      )}

      {success && (
        <div className="bg-green-100 border border-green-400 text-green-700 px-4 py-3 rounded mb-4">
          {success}
        </div>
      )}

      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        {clients.map((client) => (
          <div
            key={client.id}
            className="bg-white rounded-lg shadow-md p-6 hover:shadow-lg transition-shadow"
          >
            <h2 className="text-xl font-semibold mb-2">{client.name}</h2>
            <p className="text-gray-600 mb-2">{client.email}</p>
            {client.phone && <p className="text-gray-600 mb-2">{client.phone}</p>}
            {client.status && (
              <p className="text-gray-600 mb-2">Status: {client.status}</p>
            )}
            {client.notes && (
              <p className="text-gray-600 mb-4">Notes: {client.notes}</p>
            )}
            <button
              onClick={() => {
                setSelectedClient(client);
                setShowEmailModal(true);
              }}
              className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600 transition-colors"
            >
              Send Email
            </button>
          </div>
        ))}
      </div>

      {showEmailModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center">
          <div className="bg-white rounded-lg p-6 max-w-md w-full">
            <h2 className="text-xl font-semibold mb-4">
              Send Email to {selectedClient?.name}
            </h2>
            <div className="mb-4">
              <label className="block text-gray-700 mb-2">Subject</label>
              <input
                type="text"
                value={emailContent.subject}
                onChange={(e) =>
                  setEmailContent({ ...emailContent, subject: e.target.value })
                }
                className="w-full px-3 py-2 border rounded"
              />
            </div>
            <div className="mb-4">
              <label className="block text-gray-700 mb-2">Message</label>
              <textarea
                value={emailContent.body}
                onChange={(e) =>
                  setEmailContent({ ...emailContent, body: e.target.value })
                }
                className="w-full px-3 py-2 border rounded h-32"
              />
            </div>
            <div className="flex justify-end space-x-2">
              <button
                onClick={() => setShowEmailModal(false)}
                className="px-4 py-2 text-gray-600 hover:text-gray-800"
              >
                Cancel
              </button>
              <button
                onClick={handleSendEmail}
                disabled={isSending}
                className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600 transition-colors disabled:opacity-50"
              >
                {isSending ? "Sending..." : "Send"}
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}
