import { sendEmail, hasValidCredentials } from "../services/emailService";
// ... other imports ...

export default function ClientsPage() {
  const [hasGmailAuth, setHasGmailAuth] = useState(false);
  // ... other state variables ...

  useEffect(() => {
    // Check if we have valid Gmail credentials
    async function checkGmailAuth() {
      const isValid = await hasValidCredentials();
      console.log("Gmail credentials status:", isValid ? "Valid" : "Not valid");
      setHasGmailAuth(isValid);
    }
    checkGmailAuth();
  }, []);

  // ... rest of the component ...
}
