import { auth } from "../firebase";

const SENDER_EMAIL = "peta@kapiki.co.nz";

function createMessage(to, bcc, subject, messageText) {
  const str = [
    "From: " + SENDER_EMAIL,
    "To: " + to,
    "Bcc: " + bcc.join(","),
    "Subject: " + subject,
    "",
    messageText,
  ].join("\n");

  const encodedMessage = btoa(str)
    .replace(/\+/g, "-")
    .replace(/\//g, "_")
    .replace(/=+$/, "");

  return encodedMessage;
}

async function getAccessToken() {
  const user = auth.currentUser;
  if (!user) {
    throw new Error("User not authenticated");
  }
  return await user.getIdToken();
}

export async function sendEmail(recipientEmails, subject, messageText) {
  try {
    const accessToken = await getAccessToken();
    const message = createMessage(
      SENDER_EMAIL,
      recipientEmails,
      subject,
      messageText
    );

    const response = await fetch(
      "https://gmail.googleapis.com/gmail/v1/users/me/messages/send",
      {
        method: "POST",
        headers: {
          Authorization: `Bearer ${accessToken}`,
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          raw: message,
        }),
      }
    );

    if (!response.ok) {
      const errorData = await response.json();
      throw new Error(errorData.error?.message || "Failed to send email");
    }

    return await response.json();
  } catch (error) {
    console.error("Error sending email:", error);
    throw new Error(
      "Failed to send email. Please ensure you're properly authenticated."
    );
  }
}
