import { BrowserRouter, Routes, Route, Navigate } from "react-router-dom";
import { AuthProvider } from "./contexts/AuthContext";
import PrivateRoute from "./components/PrivateRoute";
import LoginPage from "./pages/LoginPage";
import ClientsPage from "./pages/ClientsPage";
import OAuthCallback from "./pages/OAuthCallback";

function App() {
  // Log the current URL when the app renders
  console.log('App: Current URL:', window.location.href);
  console.log('App: Pathname:', window.location.pathname);

  return (
    <BrowserRouter basename="/kapikiwebsite">
      <AuthProvider>
        <Routes>
          {/* OAuth callback route must be outside PrivateRoute and first in the list */}
          <Route path="oauth-callback" element={<OAuthCallback />} />
          <Route path="/oauth-callback" element={<OAuthCallback />} />

          <Route path="/login" element={<LoginPage />} />

          <Route
            path="/clients"
            element={
              <PrivateRoute>
                <ClientsPage />
              </PrivateRoute>
            }
          />

          {/* Redirect root to clients */}
          <Route path="/" element={<Navigate to="/clients" replace />} />

          {/* Catch any other routes and redirect to clients */}
          <Route path="*" element={<Navigate to="/clients" replace />} />
        </Routes>
      </AuthProvider>
    </BrowserRouter>
  );
}

export default App;
