import React from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import { AuthProvider } from './contexts/AuthContext';
import PrivateRoute from './components/PrivateRoute';
import HomePage from './pages/HomePage';
import LoginPage from './pages/LoginPage';
import ClientsPage from './pages/ClientsPage';
import OAuthCallback from './services/oauthCallback';

// Get the base URL considering the basename
const getBaseUrl = () => {
  const isLocal = window.location.hostname === "localhost";
  return isLocal ? "/kapikiwebsite" : "/Ka-Piki";
};

function App() {
  // Log the current URL when the app renders
  console.log('App: Current URL:', window.location.href);
  console.log('App: Pathname:', window.location.pathname);

  return (
    <AuthProvider>
      <Router basename={getBaseUrl()}>
        <Routes>
          <Route path="/" element={<HomePage />} />
          <Route path="/login" element={<LoginPage />} />
          <Route path="/oauth-callback" element={<OAuthCallback />} />
          <Route
            path="/clients"
            element={
              <PrivateRoute>
                <ClientsPage />
              </PrivateRoute>
            }
          />
        </Routes>
      </Router>
    </AuthProvider>
  );
}

export default App;
