import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import { sendEmail, hasValidCredentials, startOAuthFlow } from "../services/emailService";
import { useAuth } from "../contexts/AuthContext";
import Airtable from "airtable";

// Debug the environment variables
console.log('Raw env values:', {
  key: process.env.REACT_APP_AIRTABLE_API_KEY,
  base: process.env.REACT_APP_AIRTABLE_BASE_ID
});

// Initialize Airtable
const base = new Airtable({
  apiKey: 'patuHJGGTaXKERLC8.4f23e14326051a35f41bce0041488811711e1d2cad67892e767744474fa107cb'
}).base('app054uJcxxiTUK7p');

// Table and View IDs
const CLIENTS_TABLE_ID = 'tblY7oFqsMB7nYorj';
const CLIENTS_VIEW_ID = 'viwXaXxc30vOprTcd';
const EMAIL_TABLE_ID = 'tblRS46arAPZtfY1R';
const EMAIL_VIEW_ID = 'viwtidKFNeXp0cfkQ';

export default function ClientsPage() {
  const [clients, setClients] = useState([]);
  const [selectedClients, setSelectedClients] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [isSending, setIsSending] = useState(false);
  const [emailTemplate, setEmailTemplate] = useState(null);
  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");
  const navigate = useNavigate();
  const { user } = useAuth();

  useEffect(() => {
    const checkCredentials = async () => {
      try {
        const isValid = await hasValidCredentials();
        console.log("Gmail credentials status:", isValid ? "Valid" : "Not valid");

        if (!isValid) {
          await startOAuthFlow();
          return;
        }

        setIsLoading(false);
      } catch (error) {
        console.error("Error checking credentials:", error);
        setIsLoading(false);
      }
    };

    checkCredentials();
  }, [navigate]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        // Fetch email template first
        console.log('Fetching email template...');
        const templateRecords = await base(EMAIL_TABLE_ID).select({
          view: EMAIL_VIEW_ID
        }).all();

        if (templateRecords && templateRecords.length > 0) {
          const template = templateRecords[0];
          setEmailTemplate({
            subject: template.fields.Subject || "",
            body: template.fields.Body || "",
          });
        }

        // Fetch clients
        console.log('Fetching clients from Airtable...');
        const records = await base(CLIENTS_TABLE_ID).select({
          view: CLIENTS_VIEW_ID
        }).all();

        console.log('Got records:', records);

        const clientsData = records.map(record => ({
          id: record.id,
          organizationName: record.fields.OrganizationName || "",
          contactName: record.fields.ContactName || "",
          email: record.fields.Email || "",
          websiteURL: record.fields.WebsiteURL || "",
          sourceType: record.fields.SourceType || "",
        }));

        setClients(clientsData);
      } catch (error) {
        console.error("Error fetching data:", error);
        console.error("Full error object:", JSON.stringify(error, null, 2));
        setError("Failed to fetch data: " + error.message);
      }
    };

    fetchData();
  }, []);

  const handleSendEmail = async () => {
    if (!emailTemplate || selectedClients.length === 0) return;

    setIsSending(true);
    setError("");
    setSuccess("");

    try {
      for (const clientId of selectedClients) {
        const client = clients.find(c => c.id === clientId);
        if (client) {
          await sendEmail(
            client.email,
            emailTemplate.subject,
            emailTemplate.body
          );
        }
      }
      setSuccess("Emails sent successfully!");
      setSelectedClients([]);
    } catch (error) {
      console.error("Error sending email:", error);
      setError(error.message || "Failed to send email");
    } finally {
      setIsSending(false);
    }
  };

  const toggleClient = (clientId) => {
    setSelectedClients(prev => 
      prev.includes(clientId) 
        ? prev.filter(id => id !== clientId)
        : [...prev, clientId]
    );
  };

  if (isLoading) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-500 mx-auto"></div>
          <p className="mt-4 text-gray-600">Checking Gmail credentials...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="container mx-auto px-4 py-8">
      <div className="flex justify-between items-center mb-6">
        <h1 className="text-3xl font-bold text-gray-800">Clients</h1>
        <button
          onClick={handleSendEmail}
          disabled={isSending || selectedClients.length === 0}
          className="bg-blue-500 text-white px-6 py-2 rounded-lg hover:bg-blue-600 transition-colors disabled:opacity-50 shadow-sm"
        >
          {isSending ? "Sending..." : "Send Email Template"}
        </button>
      </div>

      {error && (
        <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded-lg mb-4">
          {error}
        </div>
      )}

      {success && (
        <div className="bg-green-100 border border-green-400 text-green-700 px-4 py-3 rounded-lg mb-4">
          {success}
        </div>
      )}

      <div className="bg-white rounded-xl shadow-sm overflow-hidden border border-gray-200">
        <table className="min-w-full divide-y divide-gray-200">
          <thead className="bg-gray-50">
            <tr>
              <th scope="col" className="px-6 py-4 text-left text-xs font-semibold text-gray-500 uppercase tracking-wider">
                <input
                  type="checkbox"
                  checked={selectedClients.length === clients.length}
                  onChange={() => {
                    if (selectedClients.length === clients.length) {
                      setSelectedClients([]);
                    } else {
                      setSelectedClients(clients.map(c => c.id));
                    }
                  }}
                />
              </th>
              <th scope="col" className="px-6 py-4 text-left text-xs font-semibold text-gray-500 uppercase tracking-wider">
                Organization
              </th>
              <th scope="col" className="px-6 py-4 text-left text-xs font-semibold text-gray-500 uppercase tracking-wider">
                Contact
              </th>
              <th scope="col" className="px-6 py-4 text-left text-xs font-semibold text-gray-500 uppercase tracking-wider">
                Email
              </th>
              <th scope="col" className="px-6 py-4 text-left text-xs font-semibold text-gray-500 uppercase tracking-wider">
                Website
              </th>
              <th scope="col" className="px-6 py-4 text-left text-xs font-semibold text-gray-500 uppercase tracking-wider">
                Source
              </th>
            </tr>
          </thead>
          <tbody className="bg-white divide-y divide-gray-200">
            {clients.map((client) => (
              <tr key={client.id} className="hover:bg-gray-50">
                <td className="px-6 py-4 whitespace-nowrap">
                  <input
                    type="checkbox"
                    checked={selectedClients.includes(client.id)}
                    onChange={() => toggleClient(client.id)}
                  />
                </td>
                <td className="px-6 py-4 whitespace-nowrap">
                  <div className="text-sm font-medium text-gray-900">{client.organizationName}</div>
                </td>
                <td className="px-6 py-4 whitespace-nowrap">
                  <div className="text-sm text-gray-900">{client.contactName}</div>
                </td>
                <td className="px-6 py-4 whitespace-nowrap">
                  <div className="text-sm text-gray-900">{client.email}</div>
                </td>
                <td className="px-6 py-4 whitespace-nowrap">
                  <a href={client.websiteURL} target="_blank" rel="noopener noreferrer" 
                     className="text-sm text-blue-600 hover:text-blue-800 hover:underline">
                    {client.websiteURL.replace(/^https?:\/\//, '')}
                  </a>
                </td>
                <td className="px-6 py-4 whitespace-nowrap">
                  <span className={`px-3 py-1 inline-flex text-xs leading-5 font-semibold rounded-full ${
                    client.sourceType === 'LinkedIn' ? 'bg-blue-100 text-blue-800' :
                    client.sourceType === 'Referral' ? 'bg-green-100 text-green-800' :
                    client.sourceType === 'Website' ? 'bg-purple-100 text-purple-800' :
                    'bg-gray-100 text-gray-800'
                  }`}>
                    {client.sourceType}
                  </span>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}
