import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import { hasValidCredentials, startOAuthFlow } from "../services/emailService";
// ... other imports ...

export default function ClientsPage() {
  const [isLoading, setIsLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    const checkCredentials = async () => {
      try {
        const isValid = await hasValidCredentials();
        console.log("Gmail credentials status:", isValid ? "Valid" : "Not valid");

        if (!isValid) {
          // Start OAuth flow if credentials are not valid
          await startOAuthFlow();
          return; // The page will redirect to Google OAuth
        }

        setIsLoading(false);
      } catch (error) {
        console.error("Error checking credentials:", error);
        setIsLoading(false);
      }
    };

    checkCredentials();
  }, [navigate]);

  if (isLoading) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-500 mx-auto"></div>
          <p className="mt-4 text-gray-600">Checking Gmail credentials...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="container mx-auto px-4 py-8">
      <h1 className="text-3xl font-bold mb-8">Clients</h1>
      {/* Rest of your clients page content */}
    </div>
  );
}
