import { auth } from "../firebase";

const SENDER_EMAIL = "peta@kapiki.co.nz";

async function getServiceAccountCredentials() {
  const response = await fetch("/emailtoken.json");
  if (!response.ok) {
    throw new Error("Failed to load service account credentials");
  }
  return await response.json();
}

async function createJWT() {
  const credentials = await getServiceAccountCredentials();
  const now = Math.floor(Date.now() / 1000);

  const header = {
    alg: "RS256",
    typ: "JWT",
    kid: credentials.private_key_id,
  };

  const payload = {
    iss: credentials.client_email,
    scope: "https://www.googleapis.com/auth/gmail.send",
    aud: credentials.token_uri,
    exp: now + 3600,
    iat: now,
  };

  const encodedHeader = btoa(JSON.stringify(header))
    .replace(/\+/g, "-")
    .replace(/\//g, "_")
    .replace(/=+$/, "");
  const encodedPayload = btoa(JSON.stringify(payload))
    .replace(/\+/g, "-")
    .replace(/\//g, "_")
    .replace(/=+$/, "");

  const privateKeyBuffer = new TextEncoder().encode(credentials.private_key);
  const key = await crypto.subtle.importKey(
    "pkcs8",
    privateKeyBuffer,
    { name: "RSASSA-PKCS1-v1_5", hash: { name: "SHA-256" } },
    false,
    ["sign"]
  );

  const signature = await crypto.subtle.sign(
    { name: "RSASSA-PKCS1-v1_5", hash: { name: "SHA-256" } },
    key,
    new TextEncoder().encode(`${encodedHeader}.${encodedPayload}`)
  );

  const encodedSignature = btoa(
    String.fromCharCode(...new Uint8Array(signature))
  )
    .replace(/\+/g, "-")
    .replace(/\//g, "_")
    .replace(/=+$/, "");

  return `${encodedHeader}.${encodedPayload}.${encodedSignature}`;
}

function createMessage(to, bcc, subject, messageText) {
  const str = [
    "From: " + SENDER_EMAIL,
    "To: " + to,
    "Bcc: " + bcc.join(","),
    "Subject: " + subject,
    "",
    messageText,
  ].join("\n");

  const encodedMessage = btoa(str)
    .replace(/\+/g, "-")
    .replace(/\//g, "_")
    .replace(/=+$/, "");

  return encodedMessage;
}

export async function sendEmail(recipientEmails, subject, messageText) {
  try {
    const message = createMessage(
      SENDER_EMAIL,
      recipientEmails,
      subject,
      messageText
    );
    const credentials = await getServiceAccountCredentials();

    // Get the access token using the service account
    const tokenResponse = await fetch(credentials.token_uri, {
      method: "POST",
      headers: {
        "Content-Type": "application/x-www-form-urlencoded",
      },
      body: new URLSearchParams({
        grant_type: "urn:ietf:params:oauth:grant-type:jwt-bearer",
        assertion: await createJWT(),
      }),
    });

    if (!tokenResponse.ok) {
      const errorData = await tokenResponse.json();
      throw new Error(errorData.error?.message || "Failed to get access token");
    }

    const { access_token } = await tokenResponse.json();

    const response = await fetch(
      "https://gmail.googleapis.com/gmail/v1/users/me/messages/send",
      {
        method: "POST",
        headers: {
          Authorization: `Bearer ${access_token}`,
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          raw: message,
        }),
      }
    );

    if (!response.ok) {
      const errorData = await response.json();
      throw new Error(errorData.error?.message || "Failed to send email");
    }

    return await response.json();
  } catch (error) {
    console.error("Error sending email:", error);
    throw new Error(
      "Failed to send email. Please ensure you're properly authenticated with Gmail."
    );
  }
}
