import { auth } from "../firebase";

const SENDER_EMAIL = "peta@kapiki.co.nz";
const SERVICE_ACCOUNT_EMAIL =
  "kapiki-email-service@friendly-cargo-453820-h2.iam.gserviceaccount.com";
const PRIVATE_KEY =
  "-----BEGIN PRIVATE KEY-----\nMIIEvQIBADANBgkqhkiG9w0BAQEFAASCBKcwggSjAgEAAoIBAQCqDl7viIMbeV4J\nfUG2ulH7nnyEo9JOYNMQVGlOiA0o2WqUhXv5FRlW47VEuHueiqZ2JIlOGJl4T96V\nn1zDn5aNPxJSy5DtJjl9XUAXDvrH9KG77FS8/h3qDhG+0DYUJXbJQZAAHjUL1b/e\n5sWVgy5T/7x696CA59VTmGjk3k4ABwTD0Fzguj2afOhir/7TE1MXyliDsY0y6TNN\nBFzAXeHAI/DtaO8monvc9UdnIM1sbOwZnyzlbDJdiYtNaza701M4Dii6pEkoFrQg\njzRIRB8lzpfsR7oDheHLeHbm/9xzfrTYJSiWF6QHRCJGNKEpEyAuojREGmpRX4z3\npkxo+cLrAgMBAAECggEAK11OyDY/eJoSaMkQINCSZ1+a0RgVYPc/20IrOGNpN6l5\njDYdzv6aX5Xhe4yvuVYR7L6UZN3RBOUZFX514Uyoz4vt2D8gzdiZgGZvGXIwcRzo\nwUsasLb8WdsMm2Ps5mZHTDrzxZPuYv4211hxuhahkbn9Dj5nJ3f25kJPPU2TBl5Z\nUXSHfsUnlPqksUjkMJ4y9ou4iAmgfJSrd7xr37wfafbozxUmcjhm1E+W6V0ask9Z\nCH2FlxLeucKd7l/Pj+kPFXdWmt9Yd1CwMsf8jxE61DJu4IZVyUM3Y23IVfk7d0LC\nqk2yiJyK25DRn7nriXAvTBPaGWNAoc0EMtaRq0cIQQKBgQDZHGCv3oP+hq9QrbjJ\nBpXdiqCwXf5FitrTbdiFwddzCvIKokPNpNp28S7YhyXzEGGrMX6In1yR2emWHvkk\nRqx5KBEoYclwRrrdqkvPG3ifpfT2bbj/mZl9j3KtCRAKVZAEIdldXu0+XCxcKW15\n3OUxH+ShbzBWWvL4X93egImENwKBgQDIhExeKRxKr8VbOzD347zCzkFSjVd8Augj\nokRqmWyWkJRDF5WLRj3FP0DuL7I+PnqY5dI9wRdCNHBqE2W5+7LQxYELV6kqJjZY\njaq2ULwmkegLVFJWqrw4gYzG/acBiPFbV4/bDMdTh57dBTHXd98gAs4lMdhme0z+\nebdy53KE7QKBgBpbS5GOHdAEYbwVmt0so3zlm2RNxNSEatRH/YUfZxxcM83+rOSG\nIs9VsC4xGfZMAs9ZjtbfIcnC/oxu7iftPHro1kxksqOisoS+a72UxCv9ZaOU5mSB\nLRUFGRLQitX2VSlrQowHPh3X6rq1fMjSfDBegs7mRKP3Ho/wc2rTh945AoGBAMhP\nCW8jCGU/1BrANbndIktE+hJ6qgNzb+1xA4GuBMEga0veuoBoSfrKppWKxjfR+GzX\nYUBZrV9wvT/qYrv5Sfeu5ausBYzaoX2y/++jFWI+bZ0rvhlf9ClKmZSL/hLVd660\n+TttN44lNDk9ID5MPHRg+QhFFBXEZPTuKKm7tjY9AoGAOsc64WeKS6hiCsblScph\nJz6HthPkFupeeTaAmVgSQVlBZvpQkmPC7aPZwHGmuJA+n5TgvupP5qGAT9KyVJh4\n5Ztw5E6Rb2WKafKxLbUSn2fmnTEyxWTU/ldwH+1eBZHiBsrHAciM793uDrWXTguv\n50CL4JAh1uSFM+tRaX33n34=\n-----END PRIVATE KEY-----\n";

async function createJWT() {
  const header = {
    alg: "RS256",
    typ: "JWT",
    kid: "248488355de241a90e722e0cea6b5bd8f4a21095",
  };

  const now = Math.floor(Date.now() / 1000);
  const payload = {
    iss: SERVICE_ACCOUNT_EMAIL,
    scope: "https://www.googleapis.com/auth/gmail.send",
    aud: "https://oauth2.googleapis.com/token",
    exp: now + 3600,
    iat: now,
  };

  const encodedHeader = btoa(JSON.stringify(header))
    .replace(/\+/g, "-")
    .replace(/\//g, "_")
    .replace(/=+$/, "");
  const encodedPayload = btoa(JSON.stringify(payload))
    .replace(/\+/g, "-")
    .replace(/\//g, "_")
    .replace(/=+$/, "");

  // Sign the JWT with the private key
  const signature = await crypto.subtle.sign(
    { name: "RSASSA-PKCS1-v1_5", hash: { name: "SHA-256" } },
    await crypto.subtle.importKey(
      "pkcs8",
      new TextEncoder().encode(PRIVATE_KEY),
      { name: "RSASSA-PKCS1-v1_5", hash: { name: "SHA-256" } },
      false,
      ["sign"]
    ),
    new TextEncoder().encode(`${encodedHeader}.${encodedPayload}`)
  );

  const encodedSignature = btoa(
    String.fromCharCode(...new Uint8Array(signature))
  )
    .replace(/\+/g, "-")
    .replace(/\//g, "_")
    .replace(/=+$/, "");

  return `${encodedHeader}.${encodedPayload}.${encodedSignature}`;
}

function createMessage(to, bcc, subject, messageText) {
  const str = [
    "From: " + SENDER_EMAIL,
    "To: " + to,
    "Bcc: " + bcc.join(","),
    "Subject: " + subject,
    "",
    messageText,
  ].join("\n");

  const encodedMessage = btoa(str)
    .replace(/\+/g, "-")
    .replace(/\//g, "_")
    .replace(/=+$/, "");

  return encodedMessage;
}

export async function sendEmail(recipientEmails, subject, messageText) {
  try {
    const message = createMessage(
      SENDER_EMAIL,
      recipientEmails,
      subject,
      messageText
    );

    // Get the access token using the service account
    const tokenResponse = await fetch("https://oauth2.googleapis.com/token", {
      method: "POST",
      headers: {
        "Content-Type": "application/x-www-form-urlencoded",
      },
      body: new URLSearchParams({
        grant_type: "urn:ietf:params:oauth:grant-type:jwt-bearer",
        assertion: await createJWT(),
      }),
    });

    if (!tokenResponse.ok) {
      throw new Error("Failed to get access token");
    }

    const { access_token } = await tokenResponse.json();

    const response = await fetch(
      "https://gmail.googleapis.com/gmail/v1/users/me/messages/send",
      {
        method: "POST",
        headers: {
          Authorization: `Bearer ${access_token}`,
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          raw: message,
        }),
      }
    );

    if (!response.ok) {
      const errorData = await response.json();
      throw new Error(errorData.error?.message || "Failed to send email");
    }

    return await response.json();
  } catch (error) {
    console.error("Error sending email:", error);
    throw new Error(
      "Failed to send email. Please ensure you're properly authenticated with Gmail."
    );
  }
}
