import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import { sendEmail, hasValidCredentials, startOAuthFlow } from "../services/emailService";
import { useAuth } from "../contexts/AuthContext";
import Airtable from "airtable";

// Debug the environment variables
console.log('Raw env values:', {
  key: process.env.REACT_APP_AIRTABLE_API_KEY,
  base: process.env.REACT_APP_AIRTABLE_BASE_ID
});

// Initialize Airtable
const base = new Airtable({
  apiKey: 'patuHJGGTaXKERLC8.4f23e14326051a35f41bce0041488811711e1d2cad67892e767744474fa107cb'
}).base('app054uJcxxiTUK7p');

// Table and View IDs
const CLIENTS_TABLE_ID = 'tblY7oFqsMB7nYorj';
const CLIENTS_VIEW_ID = 'viwXaXxc30vOprTcd';
const EMAIL_TABLE_ID = 'tblRS46arAPZtfY1R';
const EMAIL_VIEW_ID = 'viwtidKFNeXp0cfkQ';

export default function ClientsPage() {
  const [clients, setClients] = useState([]);
  const [selectedClients, setSelectedClients] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [isSending, setIsSending] = useState(false);
  const [emailContent, setEmailContent] = useState({
    subject: "",
    body: "",
  });
  const [showEmailModal, setShowEmailModal] = useState(false);
  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");
  const navigate = useNavigate();
  const { user } = useAuth();

  useEffect(() => {
    const checkCredentials = async () => {
      try {
        const isValid = await hasValidCredentials();
        console.log("Gmail credentials status:", isValid ? "Valid" : "Not valid");

        if (!isValid) {
          await startOAuthFlow();
          return;
        }

        setIsLoading(false);
      } catch (error) {
        console.error("Error checking credentials:", error);
        setIsLoading(false);
      }
    };

    checkCredentials();
  }, [navigate]);

  useEffect(() => {
    const fetchClients = async () => {
      try {
        console.log('Fetching clients from Airtable...');
        const records = await base(CLIENTS_TABLE_ID).select({
          view: CLIENTS_VIEW_ID
        }).all();

        console.log('Got records:', records);

        const clientsData = records.map(record => ({
          id: record.id,
          name: record.fields.Name || "",
          email: record.fields.Email || "",
          phone: record.fields.Phone || "",
          status: record.fields.Status || "",
          notes: record.fields.Notes || "",
        }));

        setClients(clientsData);
      } catch (error) {
        console.error("Error fetching clients:", error);
        console.error("Full error object:", JSON.stringify(error, null, 2));
        setError("Failed to fetch clients: " + error.message);
      }
    };

    fetchClients();
  }, []);

  const handleSendEmail = async () => {
    if (selectedClients.length === 0) return;

    setIsSending(true);
    setError("");
    setSuccess("");

    try {
      // Send email to all selected clients
      for (const clientId of selectedClients) {
        const client = clients.find(c => c.id === clientId);
        if (client) {
          await sendEmail(
            client.email,
            emailContent.subject,
            emailContent.body
          );
        }
      }
      setSuccess("Emails sent successfully!");
      setShowEmailModal(false);
      setEmailContent({ subject: "", body: "" });
      setSelectedClients([]);
    } catch (error) {
      console.error("Error sending email:", error);
      setError(error.message || "Failed to send email");
    } finally {
      setIsSending(false);
    }
  };

  const toggleSelectAll = () => {
    if (selectedClients.length === clients.length) {
      setSelectedClients([]);
    } else {
      setSelectedClients(clients.map(client => client.id));
    }
  };

  const toggleClient = (clientId) => {
    if (selectedClients.includes(clientId)) {
      setSelectedClients(selectedClients.filter(id => id !== clientId));
    } else {
      setSelectedClients([...selectedClients, clientId]);
    }
  };

  if (isLoading) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-500 mx-auto"></div>
          <p className="mt-4 text-gray-600">Checking Gmail credentials...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="container mx-auto px-4 py-8">
      <div className="flex justify-between items-center mb-6">
        <h1 className="text-3xl font-bold">Clients</h1>
        {selectedClients.length > 0 && (
          <button
            onClick={() => setShowEmailModal(true)}
            className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600 transition-colors"
          >
            Send Email ({selectedClients.length})
          </button>
        )}
      </div>

      {error && (
        <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded mb-4">
          {error}
        </div>
      )}

      {success && (
        <div className="bg-green-100 border border-green-400 text-green-700 px-4 py-3 rounded mb-4">
          {success}
        </div>
      )}

      <div className="bg-white rounded-lg shadow overflow-hidden">
        <table className="min-w-full divide-y divide-gray-200">
          <thead className="bg-gray-50">
            <tr>
              <th scope="col" className="px-6 py-3 text-left">
                <input
                  type="checkbox"
                  checked={selectedClients.length === clients.length}
                  onChange={toggleSelectAll}
                  className="h-4 w-4 text-blue-600 rounded border-gray-300"
                />
              </th>
              <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                Client Name
              </th>
              <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                Contact Details
              </th>
              <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                Status
              </th>
              <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                Notes
              </th>
            </tr>
          </thead>
          <tbody className="bg-white divide-y divide-gray-200">
            {clients.map((client) => (
              <tr 
                key={client.id}
                className={selectedClients.includes(client.id) ? "bg-blue-50 hover:bg-blue-100" : "hover:bg-gray-50"}
              >
                <td className="px-6 py-4 whitespace-nowrap">
                  <input
                    type="checkbox"
                    checked={selectedClients.includes(client.id)}
                    onChange={() => toggleClient(client.id)}
                    className="h-4 w-4 text-blue-600 rounded border-gray-300"
                  />
                </td>
                <td className="px-6 py-4">
                  <div className="text-sm font-medium text-gray-900">{client.name}</div>
                </td>
                <td className="px-6 py-4">
                  <div className="text-sm text-gray-900">
                    <div className="mb-1">
                      <span className="font-medium">Email:</span> {client.email}
                    </div>
                    {client.phone && (
                      <div>
                        <span className="font-medium">Phone:</span> {client.phone}
                      </div>
                    )}
                  </div>
                </td>
                <td className="px-6 py-4">
                  <span className={`px-2 inline-flex text-xs leading-5 font-semibold rounded-full ${
                    client.status === 'Active' ? 'bg-green-100 text-green-800' :
                    client.status === 'Inactive' ? 'bg-red-100 text-red-800' :
                    'bg-gray-100 text-gray-800'
                  }`}>
                    {client.status || 'Not Set'}
                  </span>
                </td>
                <td className="px-6 py-4">
                  <div className="text-sm text-gray-500 max-w-xs overflow-hidden text-ellipsis">
                    {client.notes || '-'}
                  </div>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {showEmailModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center">
          <div className="bg-white rounded-lg p-6 max-w-md w-full">
            <h2 className="text-xl font-semibold mb-4">
              Send Email to {selectedClients.length} client{selectedClients.length !== 1 ? 's' : ''}
            </h2>
            <div className="mb-4">
              <label className="block text-gray-700 mb-2">Subject</label>
              <input
                type="text"
                value={emailContent.subject}
                onChange={(e) =>
                  setEmailContent({ ...emailContent, subject: e.target.value })
                }
                className="w-full px-3 py-2 border rounded"
              />
            </div>
            <div className="mb-4">
              <label className="block text-gray-700 mb-2">Message</label>
              <textarea
                value={emailContent.body}
                onChange={(e) =>
                  setEmailContent({ ...emailContent, body: e.target.value })
                }
                className="w-full px-3 py-2 border rounded h-32"
              />
            </div>
            <div className="flex justify-end space-x-2">
              <button
                onClick={() => setShowEmailModal(false)}
                className="px-4 py-2 text-gray-600 hover:text-gray-800"
              >
                Cancel
              </button>
              <button
                onClick={handleSendEmail}
                disabled={isSending}
                className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600 transition-colors disabled:opacity-50"
              >
                {isSending ? "Sending..." : "Send"}
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}
